import React, {useState} from 'react'
import './signup.css'

import person from '../Assets/person.png';
import email_icon from '../Assets/password.png';
import password_icon from '../Assets/email.png';


 const Signup = () => {
 const[action,setAction] = useState('Create Your Account')

 const handleToggleAction = () => {
  // Toggle between 'Sign Up' and 'Login' when the user clicks 'Not a member? Sign Up' text
  setAction(action === 'Sign Up' ? 'Login' : 'Sign Up');
};


  return (
    <div className='container'>
      <div className='header'>
        <div className='text'>{action}</div>
        <div className="underline"></div>
      </div>
      <div className='inputs'>
    <div className='input'>
        <img src={person} alt='person'/>
        <input type='text' placeholder='Username'/>
    </div>
    {/* <div className ='input'>
        <img src={email_icon} alt="email"/>
        <input type='email' placeholder='Email'/>
      </div> */}
      <div className ='input'>
        <img src={password_icon} alt="password"/>
        <input type='password' placeholder='Password'/>
      </div>
    </div>
    <div className="forgot-password">
    {action === 'Login' ? "Not a member? " : "Already a member? "}
        <span onClick={handleToggleAction}>
          {/* Display different link text based on action state */}
          {action === 'Login' ? "Sign Up" : "Login"}
        </span>
        </div>
    <div className='submit-container'>
      {/* <div className={action ==="Login"?"submit gray":"submit"} onClick={()=>{setAction("Sign Up")}}>Sign Up</div>
      <div className={action ==="Sign Up"?"submit gray":"submit"} onClick={()=>{setAction("Login")}}>Login</div>
       */}
         {action === 'Sign Up' && (
          <div className='submit' onClick={() => { setAction('Sign Up') }}>Sign Up</div>
        )}
        {action === 'Login' && (
          <div className='submit' onClick={() => { setAction('Login') }}>Login</div>
        )}
    </div>
    </div>

 

  )
}
export default Signup;
